    private void bIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bIngresarActionPerformed
        // TODO add your handling code here:
        String usuario,clave;
        usuario= tUsuario.getText();
        clave= tPass.getText();
        if ("administrador".equals(usuario) & "admin".equals(clave)){
            menu me=new menu();
            me.setLocationRelativeTo(null);
            me.setVisible(true);
            this.setVisible(false);
            
        }else{
            JOptionPane.showMessageDialog(null,"Usted ingreso una clave o usuario incorrecto" , "Ingreso incorrecto", JOptionPane.ERROR_MESSAGE);
            tUsuario.setText(null);
            tPass.setText(null);
            tUsuario.requestFocus();
        }
    }//GEN-LAST:event_bIngresarActionPerformed

